void graphs_screenViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &close_btn)
    {
        //go_home
        //When close_btn clicked change screen to home_screen
        //Go to home_screen with screen transition towards North
        application().gotohome_screenScreenSlideTransitionNorth();
    }
    if (&src == &day_btn)
    {
        //graphDay
        //When day_btn clicked call virtual function
        //Call graphDay
        graphDay();
    }
    if (&src == &hour_btn)
    {
        //graphHour
        //When hour_btn clicked call virtual function
        //Call graphHour
        graphHour();
    }
    if (&src == &month_btn)
    {
        //graphMonth
        //When month_btn clicked call virtual function
        //Call graphMonth
        graphMonth();
    }
    if (&src == &live_btn)
    {
        //graphLive
        //When live_btn clicked call virtual function
        //Call graphLive
        graphLive();
    }
}
